import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  const [file, setFile] = useState()
  const [triggers, setTriggers] = useState([])
  const [counter, setCounter] = useState(0)

  function handleChange(event) {
    setFile(event.target.files[0])
    var reader = new FileReader();
    reader.onload = function(e) {
      var data = JSON.parse(e.target.result);
      console.log(data.Triggers);
      setTriggers(data.Triggers);

    };

    reader.readAsText(event.target.files[0])

  }

  function incrementCounter() {
    if(counter < triggers.length - 1){
      setCounter(counter + 1)
    }
  }

  function renderTrigger() {
    return (
      <div>
        <p>{counter}</p>
        <h3>Position</h3>
        <p>X: {triggers[counter].Position[0]}</p>
        <p>Y: {triggers[counter].Position[1]}</p>
        <p>Z: {triggers[counter].Position[2]}</p>

        <h3>Orientation</h3>
        <p>X: {triggers[counter].Orientation[0]}</p>
        <p>Y: {triggers[counter].Orientation[1]}</p>
        <p>Z: {triggers[counter].Orientation[2]}</p>

        <h3>Size</h3>
        <p>X: {triggers[counter].Size[0]}</p>
        <p>Y: {triggers[counter].Size[1]}</p>
        <p>Z: {triggers[counter].Size[2]}</p>

        <button onClick={incrementCounter}>Next</button>
        <button 
          type='button'
          href={`data:text/json;charset=utf-8,${encodeURIComponent(
          JSON.stringify(triggers)
          )}`}
          download= "cfgundergroundtriggers.json"
        >
          Download File
        </button>

      </div>
    )
  }

  return (
    <div className="App">
      <form>
        <h1>Upload </h1>
        <input type="file" onChange={handleChange}/>
        <button type="submit">Upload</button>
      </form>

      {triggers[0] === undefined ? <p>empty</p> : renderTrigger()}
    </div>
  );
}

export default App;
