import { InputGroup, InputGroupText, Input } from "reactstrap";
import { useState } from 'react';

function Orientation({triggers, index, updateTrigger}) {
    const [tempX, setTempX] = useState(triggers[index]?.Orientation[0])
    const [tempY, setTempY] = useState(triggers[index]?.Orientation[1])
    const [tempZ, setTempZ] = useState(triggers[index]?.Orientation[2])

    function changeX(value) {
        setTempX(value)
    }

    function changeY(value) {
        setTempY(value)
    }

    function changeZ(value) {
        setTempZ(value)
    }

    function updateOrientation() {
        var temp = triggers
        temp[index].Orientation[0] = parseFloat(tempX);

        temp[index].Orientation[1] = parseFloat(tempY);

        temp[index].Orientation[2] = parseFloat(tempZ);


        updateTrigger(temp)

        setTempX(0)
        setTempY(0)
        setTempZ(0)



    }
    return (
        <>
            <h3>Orientation</h3>
            <InputGroup id='inputGroup'>
                <InputGroupText>X: {triggers[index].Orientation[0]}</InputGroupText>
                <Input value={tempX} onChange={e => setTempX(e.target.value)}></Input>
                {/* <input type="text" value={tempX} onChange={e => setTempX(e.target.value)}></input> */}
            </InputGroup>
            <br />
            <InputGroup id='inputGroup'>
                <InputGroupText>Y: {triggers[index]?.Orientation[1]}</InputGroupText>
                <Input value={tempY} onChange={e => changeY(e.target.value)}></Input>
            </InputGroup>
            <br />
            <InputGroup id='inputGroup'>
                <InputGroupText>Z: {triggers[index]?.Orientation[2]}</InputGroupText>
                <Input value={tempZ} onChange={e => changeZ(e.target.value)}></Input>
            </InputGroup>
            <br />

            <input type='submit' value='submit' onClick={updateOrientation} />
        </>
    );
}

export default Orientation;
