import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  const [file, setFile] = useState()
  const [triggers, setTriggers] = useState([])
  const [counter, setCounter] = useState(0)

  function handleChange(event) {
    setFile(event.target.files[0])
    var reader = new FileReader();
    reader.onload = function(e) {
      var data = JSON.parse(e.target.result);
      console.log(data.Triggers);
      setTriggers(data.Triggers);

    };

    reader.readAsText(event.target.files[0])

  }

  function renderTrigger() {
    return (
      <div>
        <h3>Position</h3>
        <p>X: {triggers[0].Position[0]}</p>
        <p>Y: {triggers[0].Position[1]}</p>
        <p>Z: {triggers[0].Position[2]}</p>

        <h3>Orientation</h3>
        <p>X: {triggers[0].Orientation[0]}</p>
        <p>Y: {triggers[0].Orientation[1]}</p>
        <p>Z: {triggers[0].Orientation[2]}</p>

        <h3>Size</h3>
        <p>X: {triggers[0].Size[0]}</p>
        <p>Y: {triggers[0].Size[1]}</p>
        <p>Z: {triggers[0].Size[2]}</p>

        <button>Next</button>

      </div>
    )
  }

  return (
    <div className="App">
      <form>
        <h1>Upload file</h1>
        <input type="file" onChange={handleChange}/>
        <button type="submit">Upload</button>
      </form>

      {triggers[0] === undefined ? <p>empty</p> : renderTrigger()}
    </div>
  );
}

export default App;
