import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  const [data, setData] = useState([])
  const [length, setLength] = useState(0)
  const [counter, setCounter] = useState(0)

  function handleChange(event) {
    setFile(event.target.files[0])
    var reader = new FileReader();
    reader.onload = function(e) {
      var data = JSON.parse(e.target.result);
      console.log(data.Triggers);
      setData(data);
      setTriggers(data.Triggers);
      setLength(data.Triggers.length - 1);

    };

    reader.readAsText(event.target.files[0])

  }

  function incrementCounter() {
    if(counter < data.Triggers.length - 1){
      setCounter(counter + 1)
    }
  }

  const downloadFile = () =>{
    const fileName = "cfgundergroundtriggers"
    const json = JSON.stringify(data, null, 2)
    const blob = new Blob([json], {type: "application/json"})
    const href = URL.createObjectURL(blob)

    const link = document.createElement("a")
    link.href = href
    link.download = fileName + ".json"
    document.body.appendChild(link);
    link.click();

    document.body.removeChild(link);
    URL.revokeObjectURL(href);
  }

  function addExample(){
    let temp = data;

    console.log(temp.Triggers);
    data.Triggers.push(data.Triggers[0]);
    console.log(temp.Triggers);
    console.log(temp);

    setData(data);
    setLength(length + 1);
  }

  function renderTrigger() {
    return (
      <div>
        <p>{counter} / {length}</p>
        <p>{data.Triggers[counter].Position[0]}</p>
        <h3>Position</h3>
        <p>X: {data.Triggers[counter].Position[0]}</p>
        <p>Y: {data.Triggers[counter].Position[1]}</p>
        <p>Z: {data.Triggers[counter].Position[2]}</p>

        <h3>Orientation</h3>
        <p>X: {data.Triggers[counter].Orientation[0]}</p>
        <p>Y: {data.Triggers[counter].Orientation[1]}</p>
        <p>Z: {data.Triggers[counter].Orientation[2]}</p>

        <h3>Size</h3>
        <p>X: {data.Triggers[counter].Size[0]}</p>
        <p>Y: {data.Triggers[counter].Size[1]}</p>
        <p>Z: {data.Triggers[counter].Size[2]}</p>

        <button onClick={incrementCounter}>Next</button>
        <button onClick={downloadFile}> Download </button>
        <button onClick={addExample}> Add Example </button>

      </div>
    )
  }

  return (
    <div className="App">
      <form>
        <h1>Upload </h1>
        <input type="file" onChange={handleChange}/>
        <button type="submit">Upload</button>
      </form>

      {triggers[0] === undefined ? <p>empty</p> : renderTrigger()}
    </div>
  );
}

export default App;
