import { InputGroup, InputGroupText, Input } from "reactstrap";
import { useState } from "react";

function Position({triggers, index}) {
    const [tempX, setTempX] = useState(triggers[index].Position[0])
    const [tempY, setTempY] = useState(triggers[index].Position[1])
    const [tempZ, setTempZ] = useState(triggers[index].Position[2])

    function updatePosition() {
        var temp = triggers

        temp[index].Position[0] = parseFloat(tempX);

        temp[index].Position[1] = parseFloat(tempY);

        temp[index].Position[2] = parseFloat(tempZ);


        updateTrigger(temp)

        setTempX(0)
        setTempY(0)
        setTempZ(0)
    }

    return (
        <>
            <h3>Position</h3>
            <InputGroup id='inputGroup'>
                <InputGroupText>X: {triggers[index].Position[0]}</InputGroupText>
                <Input placeholder={tempX} onChange={e => setTempX(e.target.value)}></Input>
            </InputGroup>
            <br />
            <InputGroup id='inputGroup'>
                <InputGroupText>Y: {triggers[index].Position[1]}</InputGroupText>
                <Input placeholder={tempY} onChange={e => setTempY(e.target.value)}></Input>
            </InputGroup>
            <br />
            <InputGroup id='inputGroup'>
                <InputGroupText>Z: {triggers[index].Position[2]}</InputGroupText>
                <Input placeholder={tempZ} onChange={e => setTempZ(e.target.value)}></Input>
            </InputGroup>
            <br/>

            <button onClick={updatePosition}>Save</button>
        </>
    );
}

export default Position;
