import './App.css';
import { useState } from 'react';
import { Container, Row, Col } from 'reactstrap';
import Position from './Components/Position';
import Orientation from './Components/Orientation';
import Size from './Components/Size';
import Lighting from './Components/Lighting';
import Settings from './Components/Settings';

function App() {

  const [file, setFile] = useState()
  const [data, setData] = useState([])
  const [length, setLength] = useState(0)
  const [index, setIndex] = useState(0)

  function handleChange(event) {
    setFile(event.target.files[0])
    var reader = new FileReader();
    reader.onload = function(e) {
      var data = JSON.parse(e.target.result);
      console.log(data.Triggers);
      setData(data);
      setLength(data.Triggers.length - 1);

    };

    reader.readAsText(event.target.files[0])

  }

  function decrementindex() {
    if(index > 0) {
      setIndex(index - 1)
    }
  }

  function incrementindex() {
    if(index < data.Triggers.length - 1){
      setindex(index + 1)
    }
  }

  const downloadFile = () =>{
    const fileName = "cfgundergroundtriggers"
    const json = JSON.stringify(data, null, 2)
    const blob = new Blob([json], {type: "application/json"})
    const href = URL.createObjectURL(blob)

    const link = document.createElement("a")
    link.href = href
    link.download = fileName + ".json"
    document.body.appendChild(link);
    link.click();

    document.body.removeChild(link);
    URL.revokeObjectURL(href);
  }

  function addExample(){
    let temp = data;

    console.log(temp.Triggers);
    data.Triggers.push(data.Triggers[0]);
    console.log(temp.Triggers);
    console.log(temp);

    setData(data);
    setLength(length + 1);
  }

  function renderTrigger() {
    return (
      <div>
        <Container>
            <Row xs="2">
              <Col className="bg-light border">
                <Position triggers={data.Triggers} index={index}/>
              </Col>
              <Col className="bg-light border">
              <Orientation triggers={data.Triggers} index={index}/>
              </Col>
            </Row>
            <Row>
              <Col className="bg-light border">
                <Size triggers={data.Triggers} index={index}/>
              </Col>
              <Col className="bg-light border">
                <Lighting triggers={data.Triggers} index={index}/>
              </Col>
            </Row>
            <Row>
              <h3>Breadcrumbs</h3>
              <Col className='bg-light border'>
                <h3>Position</h3>
                <p>X: {data.Triggers[index].Breadcrumbs[0]?.Position[0]}</p>
                <p>Y: {data.Triggers[index].Breadcrumbs[0]?.Position[1]}</p>
                <p>Z: {data.Triggers[index].Breadcrumbs[0]?.Position[2]}</p>
              </Col>
              <Col className='bg-light border'>
                <Settings triggers={data.Triggers} index={index}/>
              </Col>
            </Row>
          </Container>
        <p>{index} / {length}</p>
        <button onClick={decrementindex}>Prev</button>
        <button onClick={incrementindex}>Next</button>
        <button onClick={downloadFile}> Download </button>
        <button onClick={addExample}> Add Example </button>

      </div>
    )
  }

  return (
    <div className="App">
      <form>
        <h1>DayZ cfgundergroundtriggers Editor </h1>
        <input type="file" onChange={handleChange}/>
      </form>

      {length < 1 ? <></> : renderTrigger()}
    </div>
  );
}

export default App;
