import { InputGroup, InputGroupText, Input } from "reactstrap";
import { useState } from 'react';

function Orientation({triggers, index, updateTrigger}) {
    const [tempX, setTempX] = useState(triggers[index]?.Orientation[0])
    const [tempY, setTempY] = useState(triggers[index]?.Orientation[1])
    const [tempZ, setTempZ] = useState(triggers[index]?.Orientation[2])

    function changeX(event) {
        setTempX(value)
    }

    function changeY(event) {
        setTempY(value)
    }

    function changeZ(value) {
        setTempZ(value)
    }

    function updateOrientation() {
        var temp = triggers
        console.log(temp[index].Orientation);
        temp[index].Orientation[0] = parseFloat(tempX);

        temp[index].Orientation[1] = parseFloat(tempY);

        temp[index].Orientation[2] = parseFloat(tempZ);


        updateTrigger(temp)
        // TODO: Get reset of values
        setTempX(null)
        setTempY(null)
        setTempZ(null)


    }
    return (
        <>
            <h3>Orientation</h3>
            {console.log(triggers)}
            <InputGroup id='inputGroup'>
                <InputGroupText>X</InputGroupText>
                <Input placeholder={triggers[index]?.Orientation[0]} onChange={changeX}></Input>
            </InputGroup>
            <br />
            <InputGroup id='inputGroup'>
                <InputGroupText>Y</InputGroupText>
                <Input placeholder={triggers[index]?.Orientation[1]} onChange={e => changeY(e.target.value)}></Input>
            </InputGroup>
            <br />
            <InputGroup id='inputGroup'>
                <InputGroupText>Z</InputGroupText>
                <Input placeholder={triggers[index]?.Orientation[2]} onChange={changeZ}></Input>
            </InputGroup>
            <br />

            <input type='submit' value='submit' onClick={updateOrientation} />
        </>
    );
}

export default Orientation;
