import { InputGroup, InputGroupText, Input } from "reactstrap";
import { useState } from 'react';

function Orientation({triggers, index, updateTrigger}) {
    const [tempX, setTempX] = useState(0)
    const [tempY, setTempY] = useState(0)
    const [tempZ, setTempZ] = useState(0)

    function changeX(event) {
        setTempX(event.target.value)
    }

    function changeY(event) {
        setTempY(event.target.value)
    }

    function changeZ(event) {
        setTempZ(event.target.value)
    }

    function updateOrientation() {
        var temp = triggers
        console.log(temp[index].Orientation);
        temp[index].Orientation[0] = parseFloat(tempX);

        temp[index].Orientation[1] = parseFloat(tempY);

        temp[index].Orientation[2] = parseFloat(tempZ);

        console.log(temp[index].Orientation);


        updateTrigger(temp)

    }
    return (
        <>
            <h3>Orientation</h3>
            {console.log(triggers)}
            <InputGroup id='inputGroup'>
                <InputGroupText>X: {tempX}</InputGroupText>
                <Input placeholder={triggers[index]?.Orientation[0] || 0} onChange={changeX}></Input>
            </InputGroup>
            <br />
            <InputGroup id='inputGroup'>
                <InputGroupText>Y</InputGroupText>
                <Input placeholder={triggers[index]?.Orientation[1] || 0} onChange={changeY}></Input>
            </InputGroup>
            <br />
            <InputGroup id='inputGroup'>
                <InputGroupText>Z</InputGroupText>
                <Input placeholder={triggers[index]?.Orientation[2] || 0} onChange={changeZ}></Input>
            </InputGroup>
            <br />

            <button onClick={updateOrientation}>Save</button>
        </>
    );
}

export default Orientation;
