import { InputGroup, InputGroupText, Input } from "reactstrap";
import { useState } from 'react';

function Orientation({triggers, index}) {
    const [tempX, setTempX] = useState(0)
    const [tempY, setTempY] = useState(0)
    const [tempZ, setTempZ] = useState(0)

    function handleChange(event) {
        console.log(event.target.value);
        setTempX(1)
    }
    return (
        <>
            <h3>Orientation</h3>
            <InputGroup id='inputGroup'>
                <InputGroupText>X: {tempX}</InputGroupText>
                <Input placeholder={triggers[index].Orientation[0]} onChange={handleChange}></Input>
            </InputGroup>
            <br />
            <InputGroup id='inputGroup'>
                <InputGroupText>Y</InputGroupText>
                <Input placeholder={triggers[index].Orientation[1]}></Input>
            </InputGroup>
            <br />
            <InputGroup id='inputGroup'>
                <InputGroupText>Z</InputGroupText>
                <Input placeholder={triggers[index].Orientation[2]}></Input>
            </InputGroup>
            <br />
        </>
    );
}

export default Orientation;
