import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  const [file, setFile] = useState()
  const [triggers, setTriggers] = useState([])
  const [counter, setCounter] = useState(1)

  function handleChange(event) {
    setFile(event.target.files[0])
    var reader = new FileReader();
    reader.onload = function(e) {
      var data = JSON.parse(e.target.result);
      console.log(data);
      console.log(data.Triggers);
      setTriggers(data);

    };

    reader.readAsText(event.target.files[0])

  }

  function incrementCounter() {
    if(counter < triggers.length - 1){
      setCounter(counter + 1)
    }
  }

  const downloadFile = () =>{
    const fileName = "cfgundergroundtriggers"
    const json = JSON.stringify(triggers, null, 2)
    const blob = new Blob([json], {type: "application/json"})
    const href = URL.createObjectURL(blob)

    const link = document.createElement("a")
    link.href = href
    link.download = fileName + ".json"
    document.body.appendChild(link);
    link.click();

    document.body.removeChild(link);
    URL.revokeObjectURL(href);
  }

  function renderTrigger() {
    let trig = triggers.Triggers;
    return (
      <div>
        {console.log('render' + triggers)}
        <p>{counter}</p>
        <h3>Position</h3>
        <p>X: {trig[counter].Position[0]}</p>
        <p>Y: {trig[counter].Position[1]}</p>
        <p>Z: {trig[counter].Position[2]}</p>

        <h3>Orientation</h3>
        <p>X: {trig[counter].Orientation[0]}</p>
        <p>Y: {trig[counter].Orientation[1]}</p>
        <p>Z: {trig[counter].Orientation[2]}</p>

        <h3>Size</h3>
        <p>X: {trig[counter].Size[0]}</p>
        <p>Y: {trig[counter].Size[1]}</p>
        <p>Z: {trig[counter].Size[2]}</p>

        <button onClick={incrementCounter}>Next</button>
        <button onClick={downloadFile}>
          download
        </button>

      </div>
    )
  }

  return (
    <div className="App">
      <form>
        <h1>Upload </h1>
        <input type="file" onChange={handleChange}/>
        <button type="submit">Upload</button>
      </form>

      {triggers === undefined ? <p>empty</p> : renderTrigger()}
    </div>
  );
}

export default App;
